package com.payment.sandbox.validation;


import static org.junit.jupiter.api.Assertions.assertEquals;

import java.util.HashMap;
import java.util.Map;

import org.junit.Before;
import org.junit.Test;
import org.junit.runner.RunWith;
import org.mockito.InjectMocks;
import org.mockito.MockitoAnnotations;
import org.mockito.junit.MockitoJUnitRunner;

import com.fasterxml.jackson.core.JsonProcessingException;
import com.fasterxml.jackson.databind.JsonMappingException;
import com.fasterxml.jackson.databind.ObjectMapper;
import com.payment.sandbox.exception.PaymentInitiationValidationException;
import com.payment.sandbox.model.PaymentInitiationModel;
import com.payment.sandbox.model.PaymentInitiationRequest;
import com.payment.sandbox.validation.impl.RequestValidator;

@RunWith(MockitoJUnitRunner.class)
public class RequestValidatorTest  {
	
	@InjectMocks
	RequestValidator requestValidator;
	
	@Before
	public void testMethod() {
		MockitoAnnotations.initMocks(this);
	}

	
	@Test
	public void requestValidation() throws PaymentInitiationValidationException, JsonMappingException, JsonProcessingException {
		try {
			ObjectMapper objectMapper = new ObjectMapper();
			String requestBody = "{\"debtorIBAN\":\"NL02RABO7134384551\",\r\n" + 
					"\"creditorIBAN\":\"NL94ABNA1008270121\",\r\n" + 
					"\"amount\":\"1.00\"}";
			PaymentInitiationRequest paymentIntiationreq = objectMapper.readValue(requestBody, PaymentInitiationRequest.class);

			Map<String, String> headers = new HashMap<String, String>();
			headers.put("x-request-id","29318e25-cebd-498c-888a-f77672f66449");
			headers.put("signature-certificate","");
			PaymentInitiationModel payInitiationModel = new PaymentInitiationModel();
			payInitiationModel.setInitiationRequest(paymentIntiationreq);
			payInitiationModel.setHeaders(headers);
			requestValidator.requestValidation(payInitiationModel);
		}catch(PaymentInitiationValidationException pe) {
			assertEquals(400, pe.getErrorCode());
		}

	}
		

	@Test
	public void amountLimitExeeded() throws PaymentInitiationValidationException, JsonMappingException, JsonProcessingException {
		try {
			ObjectMapper objectMapper = new ObjectMapper();
			String requestBody = "{\"debtorIBAN\":\"NL02RABO7134384551\",\r\n" + 
					"\"creditorIBAN\":\"NL94ABNA1008270121\",\r\n" + 
					"\"amount\":\"1.00\"}";
			PaymentInitiationRequest paymentIntiationreq = objectMapper.readValue(requestBody, PaymentInitiationRequest.class);

			Map<String, String> headers = new HashMap<String, String>();
			headers.put("x-request-id","29318e25-cebd-498c-888a-f77672f66449");
			headers.put("signature-certificate","");
			PaymentInitiationModel payInitiationModel = new PaymentInitiationModel();
			payInitiationModel.setInitiationRequest(paymentIntiationreq);
			payInitiationModel.setHeaders(headers);
			requestValidator.amountLimitExeeded(payInitiationModel);
		}catch(PaymentInitiationValidationException pe) {
			assertEquals(422, pe.getErrorCode());
		}

	}

	
}
